// - \u0415\u0441\u043B\u0438 keyOrFn \u2014 \u044D\u0442\u043E \u0444\u0443\u043D\u043A\u0446\u0438\u044F, \u0442\u043E \u0433\u0440\u0443\u043F\u043F\u0438\u0440\u043E\u0432\u043A\u0430 \u0438\u0434\u0451\u0442 \u043F\u043E \u0437\u043D\u0430\u0447\u0435\u043D\u0438\u044E, \u043A\u043E\u0442\u043E\u0440\u043E\u0435 \u0432\u043E\u0437\u0432\u0440\u0430\u0449\u0430\u0435\u0442 \u044D\u0442\u0430 \u0444\u0443\u043D\u043A\u0446\u0438\u044F.
