// \u0412\u044B\u0437\u043E\u0432 \u043C\u0435\u0442\u043E\u0434\u0430 speak \u0443 \u043A\u0430\u0436\u0434\u043E\u0433\u043E \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u0430
