	// \u041F\u043E\u0434\u043F\u0438\u0441\u043A\u0430 \u043D\u0430 \u0441\u043E\u0431\u044B\u0442\u0438\u0435
	subscribe(eventName, fn) {}

	// \u041E\u0442\u043F\u0438\u0441\u043A\u0430 \u043E\u0442 \u0441\u043E\u0431\u044B\u0442\u0438\u044F
	unsubscribe() {}

	// \u0412\u044B\u0437\u043E\u0432 \u0441\u043E\u0431\u044B\u0442\u0438\u044F
